type User {
  id: ID!
  companies: [Company!]!
}

type Company {
  id: ID!
  name: String
  users: [User!]!
  completedTasks: [Task!]!
  decisions: [Decision!]!
}

enum LegalForm {
  GMBH
  UG
  EINZELUNTERNEHMEN
  FREIBERUFLER
}

type Task {
  id: ID!
}

type Decision {
  id: ID!
  selectedTask: Task
}

type Query {
  userById(id: ID!): User
  companyById(id: ID!): Company
}

input CreateCompanyInput {
  name: String
  legalForm: LegalForm!
}

type Mutation {
  createCompany(input: CreateCompanyInput!): Company!
  markTaskComplete(companyId: ID!, taskId: ID!): Company!
  markTaskIncomplete(companyId: ID!, taskId: ID!): Company!
  makeDecision(companyId: ID!, decisionId: ID!, selectedTaskId: ID!): Decision!
  removeDecision(companyId: ID!, decisionId: ID!): Decision!
}
